import { useEventListener } from 'usehooks-ts'
import { ReactNode, useEffect, useRef, useState, RefObject } from 'react';
import { useRouter } from 'next/router';
import styles from '@/styles/movieCard.module.css';


const IMAGE_URL = 'http://image.tmdb.org/t/p/w500/'


function useHover<T extends HTMLElement = HTMLElement>(
  elementRef: RefObject<T>,
): boolean {
  const [value, setValue] = useState<boolean>(false)

  const handleMouseEnter = () => setValue(true)
  const handleMouseLeave = () => setValue(false)

  useEventListener('mouseenter', handleMouseEnter, elementRef)
  useEventListener('mouseleave', handleMouseLeave, elementRef)

  return value
}

type Props = {
    movie: {
        id: number,
        title: string,
        poster_path: string,
        overview: string,
        release_date: string,
        vote_average: number,
        name: string,
        media_type: string,
    };
}


export const  MovieCard = ({movie}:Props) =>{
    const router = useRouter()
    const name = movie ? movie.title ? movie.title : movie.name : '';
    const hoverRef = useRef(null)
    const isHover = useHover(hoverRef)
    const placeHolder = 'data:image/png;base64,'
    const dontHasPath = movie ? movie.poster_path ? false : true : true;

    const handleClick = () =>{
        router.push(`/${movie.id}`)
    }
    return(
        <div ref={hoverRef} style={{display:'inline'}} onClick={handleClick}>
            {isHover ?
        <div style={{backgroundColor: `${isHover ? '#4287f5':'#000'}`}} className={styles.Container} >
            <div style={{color: `${isHover ? '#222':'#4287f5'}`}} className={styles.Title} >{name}</div>
            <img src={dontHasPath ? placeHolder : IMAGE_URL + movie.poster_path } alt={name} className={styles.Image}/>
            <div className={styles.Vote}>‚≠ê{movie.vote_average}</div>
            <div className={styles.Type}>movie</div>
        </div>
        :
        <div style={{backgroundColor: `${isHover ? '#4287f5':'#000'}`}} className={styles.Container}>
            <div style={{color: `${isHover ? '#222':'#4287f5'}`}} className={styles.Title}>{name}</div>
            <img src={dontHasPath ? placeHolder : IMAGE_URL + movie.poster_path } alt={name} className={styles.Image}/>
            
        </div>  
        }
        </div>
    )
}

export default MovieCard;